};

Kareem.prototype.merge = function(other) {
  var ret = this.clone();
  for (var key in other._pres) {
    if (!other._pres.hasOwnProperty(key)) {
      continue;
    }
    ret._pres[key] = (ret._pres[key] || []).concat(other._pres[key].slice());
